CREATE TABLE NATION (
  N_NATIONKEY  NUMBER NOT NULL,
  N_NAME       CHAR(25) NOT NULL,
  N_REGIONKEY  NUMBER NOT NULL,
  N_COMMENT    VARCHAR2(152),
  CONSTRAINT pk_nation PRIMARY KEY (N_NATIONKEY)
);

CREATE TABLE REGION (R_REGIONKEY  NUMBER NOT NULL,R_NAME CHAR(25) NOT NULL,R_COMMENT VARCHAR2(152), cONSTRAINT pk_region PRIMARY KEY (R_REGIONKEY));

CREATE TABLE PART (P_PARTKEY     NUMBER NOT NULL,P_NAME        VARCHAR2(55) NOT NULL,P_MFGR        CHAR(25) NOT NULL,P_BRAND       CHAR(10) NOT NULL,P_TYPE        VARCHAR2(25) NOT NULL,P_SIZE        NUMBER NOT NULL,P_CONTAINER   CHAR(10) NOT NULL,P_RETAILPRICE NUMBER(15,2) NOT NULL,P_COMMENT     VARCHAR2(23) NOT NULL,CONSTRAINT pk_part PRIMARY KEY (P_PARTKEY));

CREATE TABLE SUPPLIER (S_SUPPKEY     NUMBER NOT NULL,S_NAME        CHAR(25) NOT NULL,S_ADDRESS     VARCHAR2(40) NOT NULL,S_NATIONKEY   NUMBER NOT NULL,S_PHONE       CHAR(15) NOT NULL,S_ACCTBAL     NUMBER(15,2) NOT NULL,S_COMMENT     VARCHAR2(101) NOT NULL,CONSTRAINT pk_supplier PRIMARY KEY (S_SUPPKEY));

CREATE TABLE PARTSUPP (PS_PARTKEY     NUMBER NOT NULL,PS_SUPPKEY     NUMBER NOT NULL,PS_AVAILQTY    NUMBER NOT NULL,PS_SUPPLYCOST  NUMBER(15,2) NOT NULL,PS_COMMENT     VARCHAR2(199) NOT NULL,CONSTRAINT pk_partsupp PRIMARY KEY (PS_PARTKEY, PS_SUPPKEY));

CREATE TABLE CUSTOMER (C_CUSTKEY     NUMBER NOT NULL,C_NAME        VARCHAR2(25) NOT NULL,C_ADDRESS     VARCHAR2(40) NOT NULL,C_NATIONKEY   NUMBER NOT NULL,C_PHONE       CHAR(15) NOT NULL,C_ACCTBAL     NUMBER(15,2) NOT NULL,C_MKTSEGMENT  CHAR(10) NOT NULL,C_COMMENT     VARCHAR2(117) NOT NULL,CONSTRAINT pk_customer PRIMARY KEY (C_CUSTKEY));

CREATE TABLE ORDERS (O_ORDERKEY       NUMBER(19) NOT NULL,O_CUSTKEY        NUMBER NOT NULL,O_ORDERSTATUS    CHAR(1) NOT NULL,O_TOTALPRICE     NUMBER(15,2) NOT NULL,O_ORDERDATE      DATE NOT NULL,O_ORDERPRIORITY  CHAR(15) NOT NULL,O_CLERK          CHAR(15) NOT NULL,O_SHIPPRIORITY   NUMBER NOT NULL,O_COMMENT        VARCHAR2(79) NOT NULL,CONSTRAINT pk_orders PRIMARY KEY (O_ORDERKEY));

CREATE TABLE LINEITEM (L_ORDERKEY    NUMBER(19) NOT NULL,L_PARTKEY     NUMBER NOT NULL,L_SUPPKEY     NUMBER NOT NULL,L_LINENUMBER  NUMBER NOT NULL,L_QUANTITY    NUMBER(15,2) NOT NULL,L_EXTENDEDPRICE  NUMBER(15,2) NOT NULL,L_DISCOUNT    NUMBER(15,2) NOT NULL,L_TAX         NUMBER(15,2) NOT NULL,L_RETURNFLAG  CHAR(1) NOT NULL,L_LINESTATUS  CHAR(1) NOT NULL,L_SHIPDATE    DATE NOT NULL,L_COMMITDATE  DATE NOT NULL,L_RECEIPTDATE DATE NOT NULL,L_SHIPINSTRUCT CHAR(25) NOT NULL,L_SHIPMODE     CHAR(10) NOT NULL,L_COMMENT      VARCHAR2(44) NOT NULL,CONSTRAINT pk_lineitem PRIMARY KEY (L_ORDERKEY, L_LINENUMBER))PARTITION BY HASH (L_ORDERKEY)PARTITIONS 128;

